            view.translatesAutoresizingMaskIntoConstraints = false
            return view
        }()

        let redView: UIView = {
            let view = UIView()
            view.heightAnchor.constraint(equalToConstant: 500).isActive = true
            // also needs a width
            view.widthAnchor.constraint(equalToConstant: 200).isActive = true
            view.backgroundColor = .red
