        if (matcher.matches()) {
            String confName = matcher.group(1);
            String parmName = matcher.group(2);
            Map&lt;String,String&gt; conf = confs.get(confName);
            if (conf == null) {
                conf = new HashMap&lt;&gt;();
                confs.put(confName, conf);
            }
            conf.put(parmName, props.getProperty(propName));
        }
    }
    return confs;
}

