public static int getIndex(int[] arr, int k) {
    int low = 0, high = arr.length - 1;
    while (low &lt;= high) {
        int mid = low + high &gt;&gt;&gt; 1;
        if(arr[mid] &gt; k) high = mid - 1;
        else low = mid + 1;
    }
    return low == arr.length ? -1 : low;
}

