    Set&lt;String&gt; strings,

    Boolean required) {

    public MyRecord(Set&lt;String&gt; strings, Boolean required) {
        this.strings = Objects.requireNonNullElse(strings, Set.of());
        this.required = Objects.requireNonNullElse(required, Boolean.TRUE);
    }
